import React, { useEffect, useState } from 'react';

const LottieAnimation = () => {
  const [scriptLoaded, setScriptLoaded] = useState(false);

  useEffect(() => {
    // Function to load the Lottie script
    const loadScript = () => {
      const existingScript = document.getElementById('lottie-script');
      if (!existingScript) {
        const script = document.createElement('script');
        script.src = "https://unpkg.com/@lottiefiles/lottie-player@latest/dist/lottie-player.js";
        script.id = 'lottie-script'; // Set an ID for the script tag
        script.async = true;
        script.onload = () => setScriptLoaded(true);
        document.body.appendChild(script);
      } else {
        setScriptLoaded(true); // Script already loaded
      }
    };

    loadScript();

    // Cleanup script on component unmount
    return () => {
      const script = document.getElementById('lottie-script');
      if (script) {
        document.body.removeChild(script);
      }
    };
  }, []);

  return (
    <>
      {scriptLoaded && (
        <lottie-player 
          src="https://lottie.host/1b4f304b-7a5f-443f-b49d-98a0c38d54b3/01vpJuBpfC.json" 
          background="transparent" 
          speed="1" 
          style={{ width: '300px', height: '300px' }} 
          direction="1" 
          mode="normal" 
          loop 
          autoplay>
        </lottie-player>
      )}
    </>
  );
};

export default LottieAnimation;
